      // Get sheet metadata to find correct sheet name
      const metadataResponse = await sheets.spreadsheets.get({
        spreadsheetId: this.spreadsheetId,
      });

      const sheetNames =
        metadataResponse.data.sheets?.map((s: any) => s.properties?.title) ||
