        throw new Error(`Sheet ID not found for sheet "${sheetName}". Available sheets: ${sheetNames.join(', ')}`);
      }

      // Delete the row using batchUpdate
      await sheets.spreadsheets.batchUpdate({
        spreadsheetId: this.spreadsheetId,
        requestBody: {
          requests: [
