client/src/pages/deal-evaluation.tsx.content:
import { useEffect } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import Sidebar from "@/components/sidebar";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";

export default function DealEvaluation() {
  const { toast } = useToast();
  const { user, isAuthenticated, isLoading } = useAuth();
  const queryClient = useQueryClient();

  // Redirect if not admin
  useEffect(() => {
    if (!isLoading && isAuthenticated && user?.role !== 'admin') {
      toast({
        title: "Zugriff verweigert",
        description: "Sie haben keine Berechtigung f√ºr diese Seite.",
        variant: "destructive",
      });
      window.location.href = '/';
    }
  }, [user, isAuthenticated, isLoading, toast]);

  const { data: deals, isLoading: dealsLoading } = useQuery({
    queryKey: ['/api/deals'],
    enabled: !!user && user.role === 'admin',
  });

  const updateDealStatus = useMutation({
    mutationFn: async ({ dealId, status, reviewNotes }: { dealId: string; status: string; reviewNotes?: string }) => {
      await apiRequest("PATCH", `/api/deals/${dealId}/status`, { status, reviewNotes });
    },
    onSuccess: () => {
      toast({
        title: "Status aktualisiert",
        description: "Der Deal-Status wurde erfolgreich aktualisiert.",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/deals'] });
      queryClient.invalidateQueries({ queryKey: ['/api/activities'] });
      queryClient.invalidateQueries({ queryKey: ['/api/dashboard/pipeline'] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "Sie sind nicht angemeldet. Weiterleitung zur Anmeldung...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Fehler",
        description: "Status konnte nicht aktualisiert werden.",
        variant: "destructive",
      });
    },
  });

  const getStatusBadge = (status: string) => {
    const statusConfig = {
      submitted: { label: "Eingereicht", variant: "secondary" as const },
      reviewing: { label: "In Bewertung", variant: "default" as const },
      approved: { label: "Genehmigt", variant: "default" as const },
      winner: { label: "Winner", variant: "default" as const },
      rejected: { label: "Abgelehnt", variant: "destructive" as const },
    };

    const config = statusConfig[status as keyof typeof statusConfig] || statusConfig.submitted;
    return <Badge variant={config.variant}>{config.label}</Badge>;
  };

  const formatPrice = (price: string | number) => {
    return new Intl.NumberFormat('de-DE', {
      style: 'currency',
      currency: 'EUR'
    }).format(Number(price));
  };

  if (isLoading || dealsLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary"></div>
      </div>
    );
  }

  if (!user || user.role !== 'admin') {
    return null;
  }

  return (
    <div className="flex h-screen overflow-hidden">
      <Sidebar />

      <div className="flex flex-col flex-1 overflow-hidden">
        {/* Header */}
        <header className="bg-card border-b border-border">
          <div className="flex items-center justify-between h-16 px-6">
            <h1 className="text-xl font-semibold text-foreground">Deal Bewertung</h1>
            <Button 
              variant="outline"
              onClick={() => window.location.href = '/'}
              data-testid="button-back-dashboard"
            >
              <i className="fas fa-arrow-left mr-2"></i>
              Zur√ºck zum Dashboard
            </Button>
          </div>
        </header>

        {/* Main Content */}
        <main className="flex-1 overflow-auto bg-background p-6">
          <div className="space-y-6">
            {!deals || deals.length === 0 ? (
              <Card>
                <CardContent className="p-8 text-center">
                  <i className="fas fa-inbox text-4xl text-muted-foreground mb-4"></i>
                  <h3 className="text-lg font-semibold text-foreground mb-2">Keine Deals zu bewerten</h3>
                  <p className="text-muted-foreground">
                    Es sind derzeit keine Deals zur Bewertung vorhanden.
                  </p>
                </CardContent>
              </Card>
            ) : (
              deals.map((deal: any) => (
                <Card key={deal.id}>
                  <CardHeader>
                    <div className="flex items-center justify-between">
                      <CardTitle className="flex items-center space-x-3">
                        <div className="w-10 h-10 bg-muted rounded-lg flex items-center justify-center">
                          <i className="fas fa-image text-muted-foreground text-xs"></i>
                        </div>
                        <div>
                          <h3 className="text-lg font-semibold">{deal.product?.productName || 'Unbekanntes Produkt'}</h3>
                          <p className="text-sm text-muted-foreground">ASIN: {deal.product?.asin}</p>
                        </div>
                      </CardTitle>
                      {getStatusBadge(deal.status)}
                    </div>
                  </CardHeader>
                  <CardContent>
                    <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-6">
                      <div>
                        <p className="text-sm text-muted-foreground">Eingereicht von</p>
                        <p className="font-medium">{deal.submitter?.firstName} {deal.submitter?.lastName}</p>
                      </div>
                      <div>
                        <p className="text-sm text-muted-foreground">Einkaufspreis</p>
                        <p className="font-medium">{formatPrice(deal.buyPrice)}</p>
                      </div>
                      <div>
                        <p className="text-sm text-muted-foreground">Verkaufspreis</p>
                        <p className="font-medium">{formatPrice(deal.sellPrice)}</p>
                      </div>
                      <div>
                        <p className="text-sm text-muted-foreground">Profit Margin</p>
                        <p className="font-medium text-green-600">{Number(deal.profitMargin).toFixed(1)}%</p>
                      </div>
                    </div>

                    {deal.notes && (
                      <div className="mb-6">
                        <p className="text-sm text-muted-foreground mb-2">Notizen</p>
                        <p className="text-sm bg-muted p-3 rounded-md">{deal.notes}</p>
                      </div>
                    )}

                    {deal.status === 'submitted' || deal.status === 'reviewing' ? (
                      <div className="flex items-center space-x-3">
                        <Button
                          size="sm"
                          onClick={() => updateDealStatus.mutate({ 
                            dealId: deal.id, 
                            status: 'reviewing' 
                          })}
                          disabled={updateDealStatus.isPending}
                          data-testid={`button-review-${deal.id}`}
                        >
                          <i className="fas fa-eye mr-2"></i>
                          In Bewertung
                        </Button>
                        <Button
                          size="sm"
                          variant="default"
                          onClick={() => updateDealStatus.mutate({ 
                            dealId: deal.id, 
                            status: 'approved',
                            reviewNotes: 'Deal genehmigt f√ºr weitere Pr√ºfung'
                          })}
                          disabled={updateDealStatus.isPending}
                          data-testid={`button-approve-${deal.id}`}
                        >
                          <i className="fas fa-check mr-2"></i>
                          Genehmigen
                        </Button>
                        <Button
                          size="sm"
                          onClick={() => updateDealStatus.mutate({ 
                            dealId: deal.id, 
                            status: 'winner',
                            reviewNotes: 'Ausgezeichnet als Winner - bereit f√ºr Einkaufsplanung'
                          })}
                          disabled={updateDealStatus.isPending}
                          className="bg-green-600 hover:bg-green-700"
                          data-testid={`button-winner-${deal.id}`}
                        >
                          <i className="fas fa-trophy mr-2"></i>
                          Als Winner markieren
                        </Button>
                        <Button
                          size="sm"
                          variant="destructive"
                          onClick={() => updateDealStatus.mutate({ 
                            dealId: deal.id, 
                            status: 'rejected',
                            reviewNotes: 'Deal entspricht nicht den Kriterien'
                          })}
                          disabled={updateDealStatus.isPending}
                          data-testid={`button-reject-${deal.id}`}
                        >
                          <i className="fas fa-times mr-2"></i>
                          Ablehnen
                        </Button>
                      </div>
                    ) : (
                      <div className="flex items-center space-x-2 text-sm text-muted-foreground">
                        <i className="fas fa-info-circle"></i>
                        <span>
                          Deal wurde am {new Date(deal.reviewedAt).toLocaleDateString('de-DE')} bewertet
                        </span>
                      </div>
                    )}
                  </CardContent>
                </Card>
              ))
            )}
          </div>
        </main>
      </div>
    </div>
  );
}

client/src/pages/deal-submission.tsx.content:
import { useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import Sidebar from "@/components/sidebar";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";

interface DealForm {
  productName: string;
  asin: string;
  buyPrice: string;
  sellPrice: string;
  category: string;
  notes: string;
}

export default function DealSubmission() {
  const { toast } = useToast();
  const { user } = useAuth();
  const queryClient = useQueryClient();

  const [formData, setFormData] = useState<DealForm>({
    productName: '',
    asin: '',
    buyPrice: '',
    sellPrice: '',
    category: '',
    notes: '',
  });

  const [profitMargin, setProfitMargin] = useState<string>('');

  const calculateProfitMargin = (buyPrice: string, sellPrice: string) => {
    const buy = parseFloat(buyPrice) || 0;
    const sell = parseFloat(sellPrice) || 0;

    if (buy > 0 && sell > 0) {
      const profit = sell - buy;
      const margin = (profit / sell) * 100;
      setProfitMargin(margin.toFixed(1));
    } else {
      setProfitMargin('');
    }
  };

  const handleInputChange = (field: keyof DealForm, value: string) => {
    const newFormData = { ...formData, [field]: value };
    setFormData(newFormData);

    if (field === 'buyPrice' || field === 'sellPrice') {
      calculateProfitMargin(
        field === 'buyPrice' ? value : formData.buyPrice,
        field === 'sellPrice' ? value : formData.sellPrice
      );
    }
  };

  const submitDeal = useMutation({
    mutationFn: async (dealData: any) => {
      await apiRequest("POST", "/api/deals", dealData);
    },
    onSuccess: () => {
      toast({
        title: "Deal eingereicht",
        description: "Ihr Deal wurde erfolgreich zur Bewertung eingereicht.",
      });

      // Reset form
      setFormData({
        productName: '',
        asin: '',
        buyPrice: '',
        sellPrice: '',
        category: '',
        notes: '',
      });
      setProfitMargin('');

      // Invalidate queries
      queryClient.invalidateQueries({ queryKey: ['/api/deals'] });
      queryClient.invalidateQueries({ queryKey: ['/api/activities'] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "Sie sind nicht angemeldet. Weiterleitung zur Anmeldung...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Fehler",
        description: "Deal konnte nicht eingereicht werden.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.productName || !formData.asin || !formData.buyPrice || !formData.sellPrice) {
      toast({
        title: "Unvollst√§ndige Daten",
        description: "Bitte f√ºllen Sie alle Pflichtfelder aus.",
        variant: "destructive",
      });
      return;
    }

    submitDeal.mutate({
      ...formData,
      buyPrice: parseFloat(formData.buyPrice),
      sellPrice: parseFloat(formData.sellPrice),
    });
  };

  return (
    <div className="flex h-screen overflow-hidden">
      <Sidebar />

      <div className="flex flex-col flex-1 overflow-hidden">
        {/* Header */}
        <header className="bg-card border-b border-border">
          <div className="flex items-center justify-between h-16 px-6">
            <h1 className="text-xl font-semibold text-foreground">Deal Erfassung</h1>
            <Button 
              variant="outline"
              onClick={() => window.location.href = '/'}
              data-testid="button-back-dashboard"
            >
              <i className="fas fa-arrow-left mr-2"></i>
              Zur√ºck zum Dashboard
            </Button>
          </div>
        </header>

        {/* Main Content */}
        <main className="flex-1 overflow-auto bg-background p-6">
          <Card className="max-w-2xl mx-auto">
            <CardHeader>
              <CardTitle>Neuen Deal erfassen</CardTitle>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleSubmit} className="space-y-6">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <Label htmlFor="productName">Produktname *</Label>
                    <Input
                      id="productName"
                      placeholder="z.B. Apple iPhone 15 Pro"
                      value={formData.productName}
                      onChange={(e) => handleInputChange('productName', e.target.value)}
                      data-testid="input-product-name"
                    />
                  </div>
                  <div>
                    <Label htmlFor="asin">ASIN *</Label>
                    <Input
                      id="asin"
                      placeholder="B0XXXXXXXXX"
                      value={formData.asin}
                      onChange={(e) => handleInputChange('asin', e.target.value)}
                      data-testid="input-asin"
                    />
                  </div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                  <div>
                    <Label htmlFor="buyPrice">Einkaufspreis (‚Ç¨) *</Label>
                    <Input
                      id="buyPrice"
                      type="number"
                      step="0.01"
                      placeholder="0.00"
                      value={formData.buyPrice}
                      onChange={(e) => handleInputChange('buyPrice', e.target.value)}
                      data-testid="input-buy-price"
                    />
                  </div>
                  <div>
                    <Label htmlFor="sellPrice">Verkaufspreis (‚Ç¨) *</Label>
                    <Input
                      id="sellPrice"
                      type="number"
                      step="0.01"
                      placeholder="0.00"
                      value={formData.sellPrice}
                      onChange={(e) => handleInputChange('sellPrice', e.target.value)}
                      data-testid="input-sell-price"
                    />
                  </div>
                  <div>
                    <Label htmlFor="profitMargin">Profit Margin (%)</Label>
                    <Input
                      id="profitMargin"
                      type="number"
                      step="0.1"
                      placeholder="0.0"
                      value={profitMargin}
                      readOnly
                      className="bg-muted"
                      data-testid="text-profit-margin"
                    />
                  </div>
                </div>

                <div>
                  <Label htmlFor="category">Kategorie</Label>
                  <Select value={formData.category} onValueChange={(value) => handleInputChange('category', value)}>
                    <SelectTrigger data-testid="select-category">
                      <SelectValue placeholder="Kategorie w√§hlen..." />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="electronics">Elektronik</SelectItem>
                      <SelectItem value="clothing">Bekleidung</SelectItem>
                      <SelectItem value="home">Haus & Garten</SelectItem>
                      <SelectItem value="sports">Sport & Freizeit</SelectItem>
                      <SelectItem value="beauty">Beauty & Gesundheit</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label htmlFor="notes">Notizen</Label>
                  <Textarea
                    id="notes"
                    rows={3}
                    placeholder="Zus√§tzliche Informationen zum Deal..."
                    value={formData.notes}
                    onChange={(e) => handleInputChange('notes', e.target.value)}
                    data-testid="textarea-notes"
                  />
                </div>

                <div className="flex items-center justify-end space-x-4 pt-4 border-t border-border">
                  <Button 
                    type="button" 
                    variant="outline"
                    onClick={() => window.location.href = '/'}
                    data-testid="button-cancel"
                  >
                    Abbrechen
                  </Button>
                  <Button 
                    type="submit" 
                    disabled={submitDeal.isPending}
                    data-testid="button-submit-deal"
                  >
                    {submitDeal.isPending ? 'Wird eingereicht...' : 'Deal einreichen'}
                  </Button>
                </div>
              </form>
            </CardContent>
          </Card>
        </main>
      </div>
    </div>
  );
}

client/src/pages/listing-builder.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Package, Plus, Upload, RefreshCw, CheckCircle, AlertCircle, Clock, Download } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";

export default function ListingBuilder() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [selectedSourcing, setSelectedSourcing] = useState<string>("");
  const [selectedPurchasing, setSelectedPurchasing] = useState<string>("");
  const [generatedSKU, setGeneratedSKU] = useState<string>("");

  const userRole = (user as any)?.role || 'va';
  const isAdmin = userRole === 'admin';

  // Fetch winner sourcing items
  const { data: winnerSourcing = [] } = useQuery({
    queryKey: ['/api/sourcing', 'winner'],
    queryFn: () => apiRequest('/api/sourcing?status=winner'),
    enabled: isAdmin,
  });

  // Fetch purchasing plans for sourcing
  const { data: purchasingPlans = [] } = useQuery({
    queryKey: ['/api/purchasing'],
    queryFn: () => apiRequest('/api/purchasing'),
    enabled: isAdmin && selectedSourcing !== "",
  });

  // Fetch existing listings
  const { data: listings = [], isLoading } = useQuery({
    queryKey: ['/api/listings'],
    queryFn: () => apiRequest('/api/listings'),
  });

  // Create listing mutation
  const createListingMutation = useMutation({
    mutationFn: (data: any) => apiRequest('/api/listings', 'POST', data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/listings'] });
      setIsCreateDialogOpen(false);
      toast({
        title: "Success",
        description: "Listing created successfully with auto-generated SKU",
      });
      resetForm();
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Sync listings mutations
  const syncAmazonMutation = useMutation({
    mutationFn: () => apiRequest('/api/integrations/amazon/sync', 'POST'),
    onSuccess: (result: any) => {
      queryClient.invalidateQueries({ queryKey: ['/api/listings'] });
      toast({
        title: "Amazon Sync Complete",
        description: `Synced ${result.syncedListings} listings to Amazon`,
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Sync Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const syncPrepMyBusinessMutation = useMutation({
    mutationFn: () => apiRequest('/api/integrations/prepmybusiness/sync', 'POST'),
    onSuccess: (result: any) => {
      queryClient.invalidateQueries({ queryKey: ['/api/listings'] });
      toast({
        title: "PrepMyBusiness Sync Complete",
        description: `Synced ${result.syncedJobs} jobs to PrepMyBusiness`,
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Sync Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Update listing sync status
  const updateSyncStatusMutation = useMutation({
    mutationFn: ({ id, amazonStatus, prepMyBusinessStatus }: { id: string; amazonStatus?: string; prepMyBusinessStatus?: string }) =>
      apiRequest(`/api/listings/${id}/sync`, 'PATCH', { amazonStatus, prepMyBusinessStatus }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/listings'] });
      toast({
        title: "Success",
        description: "Sync status updated",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setSelectedSourcing("");
    setSelectedPurchasing("");
    setGeneratedSKU("");
  };

  const generatePreviewSKU = () => {
    const sourcingItem = winnerSourcing.find((item: any) => item.id === selectedSourcing);
    if (!sourcingItem) return;

    const brand = (sourcingItem.brand || 'UNKNOWN').replace(/[^A-Za-z0-9]/g, '').toUpperCase();
    const buyPrice = parseFloat(sourcingItem.costPrice);
    const asin = sourcingItem.asin;

    const date = new Date();
    const dateStr = date.toISOString().slice(2, 10).replace(/-/g, '').slice(0, 6);

    // Calculate max brand length to stay under 40 chars
    const baseLength = buyPrice.toFixed(2).length + 1 + dateStr.length + 1 + asin.length + 2;
    const maxBrandLength = 40 - baseLength;

    const truncatedBrand = brand.length > maxBrandLength ? brand.substring(0, maxBrandLength) : brand;

    const sku = `${truncatedBrand}_${buyPrice.toFixed(2)}_${dateStr}_${asin}`;
    setGeneratedSKU(sku);
  };

  const onCreateListing = () => {
    if (!selectedSourcing) {
      toast({
        title: "Error",
        description: "Please select a sourcing item",
        variant: "destructive",
      });
      return;
    }

    createListingMutation.mutate({
      sourcingId: selectedSourcing,
      purchasingId: selectedPurchasing || null,
    });
  };

  const getStatusBadge = (status: string) => {
    const statusConfig = {
      draft: { label: "Draft", variant: "secondary" as const, icon: Clock },
      pending: { label: "Pending", variant: "default" as const, icon: RefreshCw },
      live: { label: "Live", variant: "default" as const, icon: CheckCircle, className: "bg-green-600 text-white" },
      error: { label: "Error", variant: "destructive" as const, icon: AlertCircle },
    };

    const config = statusConfig[status as keyof typeof statusConfig];
    if (!config) return null;

    const Icon = config.icon;
    return (
      <Badge variant={config.variant} className={config.className || ""}>
        <Icon className="h-3 w-3 mr-1" />
        {config.label}
      </Badge>
    );
  };

  const exportToCSV = (listings: any[]) => {
    const headers = ['SKU', 'Brand', 'ASIN', 'Buy Price', 'Amazon Status', 'PrepMyBusiness Status', 'Created Date'];
    const csvContent = [
      headers.join(','),
      ...listings.map(listing => [
        listing.skuCode,
        listing.brand,
        listing.asin,
        listing.buyPrice,
        listing.amazonSyncStatus,
        listing.prepMyBusinessSyncStatus,
        new Date(listing.createdAt).toLocaleDateString()
      ].join(','))
    ].join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.style.display = 'none';
    a.href = url;
    a.download = `listings_${new Date().toISOString().slice(0, 10)}.csv`;
    document.body.appendChild(a);
    a.click();
    window.URL.revokeObjectURL(url);
    document.body.removeChild(a);

    toast({
      title: "Export Complete",
      description: `Exported ${listings.length} listings to CSV`,
    });
  };

  if (!isAdmin) {
    return (
      <div className="space-y-6">
        <Card>
          <CardContent className="pt-6 text-center">
            <p className="text-muted-foreground">
              Access restricted to administrators only.
            </p>
          </CardContent>
        </Card>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="flex justify-between items-center">
          <h1 className="text-3xl font-bold">Listing Builder</h1>
        </div>
        <div className="grid gap-4">
          {[...Array(3)].map((_, i) => (
            <Card key={i} className="animate-pulse">
              <CardContent className="pt-6">
                <div className="h-4 bg-muted rounded w-3/4 mb-4"></div>
                <div className="h-3 bg-muted rounded w-1/2"></div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  const filteredPurchasingPlans = purchasingPlans.filter((plan: any) => plan.sourcingId === selectedSourcing);

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold">Listing Builder</h1>
          <p className="text-muted-foreground">
            Generate SKUs and manage Amazon/PrepMyBusiness listings
          </p>
        </div>
        <div className="flex gap-2">
          <Button
            variant="outline"
            onClick={() => exportToCSV(listings)}
            disabled={listings.length === 0}
            data-testid="button-export-csv"
          >
            <Download className="h-4 w-4 mr-2" />
            Export CSV
          </Button>
          <Button
            variant="outline"
            onClick={() => syncAmazonMutation.mutate()}
            disabled={syncAmazonMutation.isPending}
            data-testid="button-sync-amazon"
          >
            {syncAmazonMutation.isPending ? (
              <RefreshCw className="h-4 w-4 mr-2 animate-spin" />
            ) : (
              <Upload className="h-4 w-4 mr-2" />
            )}
            Sync Amazon
          </Button>
          <Button
            variant="outline"
            onClick={() => syncPrepMyBusinessMutation.mutate()}
            disabled={syncPrepMyBusinessMutation.isPending}
            data-testid="button-sync-prep"
          >
            {syncPrepMyBusinessMutation.isPending ? (
              <RefreshCw className="h-4 w-4 mr-2 animate-spin" />
            ) : (
              <Upload className="h-4 w-4 mr-2" />
            )}
            Sync PrepMyBusiness
          </Button>
          <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
            <DialogTrigger asChild>
              <Button data-testid="button-create-listing">
                <Plus className="h-4 w-4 mr-2" />
                Create Listing
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-2xl">
              <DialogHeader>
                <DialogTitle>Create New Listing</DialogTitle>
                <DialogDescription>
                  Generate a new listing with auto-generated SKU from winner deals
                </DialogDescription>
              </DialogHeader>
              <div className="space-y-4">
                <div>
                  <Label>Select Winner Deal *</Label>
                  <Select value={selectedSourcing} onValueChange={(value) => {
                    setSelectedSourcing(value);
                    setGeneratedSKU("");
                  }}>
                    <SelectTrigger data-testid="select-sourcing-deal">
                      <SelectValue placeholder="Choose a winner deal" />
                    </SelectTrigger>
                    <SelectContent>
                      {winnerSourcing.map((item: any) => (
                        <SelectItem key={item.id} value={item.id}>
                          {item.productName} - {item.brand} - ASIN: {item.asin}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                {selectedSourcing && (
                  <div>
                    <Label>Associated Purchasing Plan (Optional)</Label>
                    <Select value={selectedPurchasing} onValueChange={setSelectedPurchasing}>
                      <SelectTrigger data-testid="select-purchasing-plan">
                        <SelectValue placeholder="Choose purchasing plan (optional)" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="">No purchasing plan</SelectItem>
                        {filteredPurchasingPlans.map((plan: any) => (
                          <SelectItem key={plan.id} value={plan.id}>
                            Qty: {plan.plannedQuantity} - Budget: ‚Ç¨{parseFloat(plan.plannedBudget).toFixed(2)}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>
                )}

                {selectedSourcing && (
                  <div className="space-y-2">
                    <div className="flex justify-between items-center">
                      <Label>Generated SKU Preview</Label>
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={generatePreviewSKU}
                        data-testid="button-preview-sku"
                      >
                        Preview SKU
                      </Button>
                    </div>
                    {generatedSKU && (
                      <div className="p-3 bg-muted rounded-md">
                        <code className="text-sm font-mono">{generatedSKU}</code>
                        <p className="text-xs text-muted-foreground mt-1">
                          Format: BRAND_BuyPrice_Date_ASIN (max 40 characters)
                        </p>
                      </div>
                    )}
                  </div>
                )}

                <div className="flex justify-end gap-2">
                  <Button
                    variant="outline"
                    onClick={() => setIsCreateDialogOpen(false)}
                    data-testid="button-cancel"
                  >
                    Cancel
                  </Button>
                  <Button
                    onClick={onCreateListing}
                    disabled={createListingMutation.isPending || !selectedSourcing}
                    data-testid="button-submit-listing"
                  >
                    {createListingMutation.isPending ? "Creating..." : "Create Listing"}
                  </Button>
                </div>
              </div>
            </DialogContent>
          </Dialog>
        </div>
      </div>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center gap-2">
              <Package className="h-5 w-5 text-blue-600" />
              <div>
                <p className="text-sm text-muted-foreground">Total Listings</p>
                <p className="text-2xl font-bold">{listings.length}</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center gap-2">
              <CheckCircle className="h-5 w-5 text-green-600" />
              <div>
                <p className="text-sm text-muted-foreground">Live Listings</p>
                <p className="text-2xl font-bold text-green-600">
                  {listings.filter((l: any) => l.amazonSyncStatus === 'live').length}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center gap-2">
              <RefreshCw className="h-5 w-5 text-orange-600" />
              <div>
                <p className="text-sm text-muted-foreground">Pending Sync</p>
                <p className="text-2xl font-bold text-orange-600">
                  {listings.filter((l: any) => l.amazonSyncStatus === 'pending').length}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center gap-2">
              <AlertCircle className="h-5 w-5 text-red-600" />
              <div>
                <p className="text-sm text-muted-foreground">Sync Errors</p>
                <p className="text-2xl font-bold text-red-600">
                  {listings.filter((l: any) => l.amazonSyncStatus === 'error' || l.prepMyBusinessSyncStatus === 'error').length}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Listings */}
      <div className="grid gap-4">
        {listings.length === 0 ? (
          <Card>
            <CardContent className="pt-6 text-center">
              <p className="text-muted-foreground">No listings found.</p>
              <p className="text-sm text-muted-foreground mt-2">
                Create your first listing from a winner deal to start building your Amazon catalog.
              </p>
            </CardContent>
          </Card>
        ) : (
          listings.map((listing: any) => (
            <Card key={listing.id} data-testid={`card-listing-${listing.id}`}>
              <CardHeader>
                <div className="flex justify-between items-start">
                  <div className="space-y-1">
                    <CardTitle className="text-lg font-mono">{listing.skuCode}</CardTitle>
                    <CardDescription className="flex items-center gap-2">
                      <span>Brand: {listing.brand}</span>
                      <span>‚Ä¢ ASIN: {listing.asin}</span>
                      <span>‚Ä¢ Buy Price: ‚Ç¨{parseFloat(listing.buyPrice).toFixed(2)}</span>
                    </CardDescription>
                  </div>
                  <div className="flex flex-col gap-1 items-end">
                    <div className="flex items-center gap-2">
                      <span className="text-xs text-muted-foreground">Amazon:</span>
                      {getStatusBadge(listing.amazonSyncStatus)}
                    </div>
                    <div className="flex items-center gap-2">
                      <span className="text-xs text-muted-foreground">PrepMyBusiness:</span>
                      {getStatusBadge(listing.prepMyBusinessSyncStatus)}
                    </div>
                  </div>
                </div>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                  <div>
                    <Label className="text-muted-foreground">Generated Date</Label>
                    <p className="font-medium">{listing.generatedDate}</p>
                  </div>
                  <div>
                    <Label className="text-muted-foreground">CSV Exported</Label>
                    <p className="font-medium">{listing.csvExported ? 'Yes' : 'No'}</p>
                  </div>
                  <div>
                    <Label className="text-muted-foreground">Last Sync</Label>
                    <p className="font-medium">
                      {listing.lastSyncAt ? new Date(listing.lastSyncAt).toLocaleDateString() : 'Never'}
                    </p>
                  </div>
                  <div>
                    <Label className="text-muted-foreground">Created</Label>
                    <p className="font-medium">{new Date(listing.createdAt).toLocaleDateString()}</p>
                  </div>
                </div>

                {(listing.amazonSyncStatus === 'draft' || listing.prepMyBusinessSyncStatus === 'draft') && (
                  <div className="flex gap-2">
                    {listing.amazonSyncStatus === 'draft' && (
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => updateSyncStatusMutation.mutate({ 
                          id: listing.id, 
                          amazonStatus: 'pending' 
                        })}
                        data-testid={`button-sync-amazon-${listing.id}`}
                      >
                        Sync to Amazon
                      </Button>
                    )}
                    {listing.prepMyBusinessSyncStatus === 'draft' && (
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => updateSyncStatusMutation.mutate({ 
                          id: listing.id, 
                          prepMyBusinessStatus: 'pending' 
                        })}
                        data-testid={`button-sync-prep-${listing.id}`}
                      >
                        Sync to PrepMyBusiness
                      </Button>
                    )}
                  </div>
                )}

                {listing.syncErrors && (
                  <div className="text-sm text-red-600 bg-red-50 p-2 rounded">
                    <strong>Sync Errors:</strong> {listing.syncErrors}
                  </div>
                )}

                {listing.sourcing && (
                  <div className="text-xs text-muted-foreground">
                    Source: {listing.sourcing.productName} ‚Ä¢ 
                    Submitted by {listing.sourcing.submitter?.firstName} {listing.sourcing.submitter?.lastName}
                  </div>
                )}
              </CardContent>
            </Card>
          ))
        )}
      </div>
    </div>
  );
}

