import React, { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import {
  ExternalLink,
  Edit3,
  Upload,
  Trash2,
  Copy,
  CheckCircle2,
  Archive,
  ChevronDown,
  SquarePen,
  Target,
  Eye,
  User,
  Search,
  Bell,
  Package,
  X,
  FileText,
  Download,
  Image as ImageIcon,
  Paperclip,
} from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import Sidebar from "@/components/sidebar";
import { isUnauthorizedError } from "@/lib/authUtils";
import { GoogleSheetsSourcingItem, SourcingItem } from "@shared/schema";

interface SheetsData {
  items?: GoogleSheetsSourcingItem[];
  headers?: string[];
  success?: boolean;
}

interface FileUploadSectionProps {
  rowIndex: number;
  asin: string;
  uploadFile: any;
  deleteFile: any;
}

export function FileUploadSection({ rowIndex, asin, uploadFile, deleteFile }: FileUploadSectionProps) {
  const [isDragOver, setIsDragOver] = useState(false);
  const fileInputRef = React.useRef<HTMLInputElement>(null);

  // Fetch files for this row
  const { data: filesData } = useQuery({
    queryKey: ['/api/sourcing/files', rowIndex],
    queryFn: async () => {
      const response = await fetch(`/api/sourcing/files/${rowIndex}`);
      if (!response.ok) {
        throw new Error('Failed to fetch files');
      }
      return response.json();
    },
    enabled: !!rowIndex && rowIndex >= 0,
    staleTime: 5 * 60 * 1000, // 5 minutes - prevent unnecessary refetches
    gcTime: 10 * 60 * 1000, // 10 minutes - keep in cache longer
    refetchOnWindowFocus: false, // Don't refetch when window regains focus
  });

  const files = filesData?.files || [];
  const handleFileSelect = (selectedFiles: FileList | null) => {
    if (!selectedFiles || selectedFiles.length === 0) return;

    const file = selectedFiles[0];
    if (file) {
      uploadFile.mutate({ rowIndex, asin, file });
    }
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(true);
  };

  const handleDragLeave = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);
  };

  const handleDrop = (e: React.DragEvent) => {
    e.preventDefault();
    setIsDragOver(false);
    const droppedFiles = e.dataTransfer.files;
    handleFileSelect(droppedFiles);
  };

  const getFileIcon = (mimeType: string) => {
    if (mimeType.startsWith('image/')) return <ImageIcon className="w-4 h-4" />;
    if (mimeType.includes('pdf')) return <FileText className="w-4 h-4" />;
    return <Paperclip className="w-4 h-4" />;
  };

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const isImage = (mimeType: string) => mimeType.startsWith('image/');

  return (
    <div className="text-gray-600 bg-gray-50 rounded-lg p-2 min-h-[80px] text-xs">
      <h5 className="text-sm font-medium text-gray-700 mb-3">Files</h5>

      {files.length === 0 ? (
        // Upload area when no files exist
        <div
          className={`border-2 border-dashed rounded-lg p-4 text-center cursor-pointer transition-colors ${
            isDragOver 
              ? 'border-blue-400 bg-blue-50' 
              : 'border-gray-300 hover:border-blue-400'
          }`}
          onDragOver={handleDragOver}
          onDragLeave={handleDragLeave}
          onDrop={handleDrop}
          onClick={() => fileInputRef.current?.click()}
        >
          <div className="text-blue-500 mb-2">
            <Upload className="w-6 h-4 mx-auto" />
          </div>
          <div className="text-xs text-gray-500">
            <span className="text-xs text-blue-600 mb-1">Click to upload</span>
            <br />
            or drag and drop
            <br />
            PDF, DOC, XLS, images up to 10MB
          </div>
          <input
            ref={fileInputRef}
            type="file"
            className="hidden"
            accept=".pdf,.doc,.docx,.xls,.xlsx,.jpg,.jpeg,.png,.gif"
            onChange={(e) => handleFileSelect(e.target.files)}
          />
        </div>
      ) : (
        // File list when files exist
        <div className="space-y-2">
          {files.map((file: any) => (
            <div key={file.id} className="flex items-center justify-between p-2 bg-white rounded border">
              <div className="flex items-center gap-2 flex-1 min-w-0">
                {getFileIcon(file.mimeType)}
                <div className="flex-1 min-w-0">
                  <div className="text-sm font-medium text-gray-900 truncate">
                    {file.originalName}
                  </div>
                  <div className="text-xs text-gray-500">
                    {formatFileSize(file.fileSize)}
                  </div>
                </div>
                {isImage(file.mimeType) && (
                  <img
                    src={`/api/sourcing/files/download/${file.id}`}
                    alt={file.originalName}
                    className="w-8 h-8 rounded object-cover"
                  />
                )}
              </div>
              <div className="flex items-center gap-1">
                <Button
                  size="sm"
                  variant="ghost"
                  className="h-6 w-6 p-0"
                  onClick={() => window.open(`/api/sourcing/files/download/${file.id}`, '_blank')}
                  data-testid={`download-file-${file.id}`}
                >
                  <Download className="w-3 h-3" />
                </Button>
                <Button
                  size="sm"
                  variant="ghost"
                  className="h-6 w-6 p-0 text-red-500 hover:text-red-700"
                  onClick={() => deleteFile.mutate(file.id)}
                  data-testid={`delete-file-${file.id}`}
                >
                  <Trash2 className="w-3 h-3" />
                </Button>
              </div>
            </div>
          ))}

          {/* Add more files button */}
          <button
            className="w-full p-2 border-2 border-dashed border-gray-300 rounded text-xs text-gray-500 hover:border-blue-400 hover:text-blue-600 transition-colors"
            onClick={() => fileInputRef.current?.click()}
            data-testid={`add-more-files-${rowIndex}`}
          >
            + Add more files
          </button>
          <input
            ref={fileInputRef}
            type="file"
            className="hidden"
            accept=".pdf,.doc,.docx,.xls,.xlsx,.jpg,.jpeg,.png,.gif"
            onChange={(e) => handleFileSelect(e.target.files)}
          />
        </div>
      )}
    </div>
  );
}

export default function SourcingInbox() {
  const { user, isLoading: isAuthLoading } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [notesModalOpen, setNotesModalOpen] = useState(false);
  const [editingNotes, setEditingNotes] = useState<{
    rowIndex: number;
    item: GoogleSheetsSourcingItem;
    currentNotes: string;
  } | null>(null);
  const [notesText, setNotesText] = useState("");

  // Fetch sourcing items directly from Google Sheets
  const {
    data: sheetsData,
    isLoading,
    refetch,
  } = useQuery({
    queryKey: ["/api/sourcing/sheets"],
    queryFn: () => apiRequest("/api/sourcing/sheets"),
    staleTime: 0,
    gcTime: 0,
    refetchOnWindowFocus: true,
    refetchInterval: 10000,
    refetchIntervalInBackground: true,
  });

  // Fetch archived items from database (only items where archived = true)
  const { data: archivedData } = useQuery({
    queryKey: ["/api/sourcing/items", "archived"],
    queryFn: () => apiRequest("/api/sourcing/items?archived=true"),
    enabled: !!user,
    staleTime: 0, // Force fresh data
    gcTime: 0,
  });

  const sourcingItems: GoogleSheetsSourcingItem[] =
    (sheetsData as any)?.items || [];
  const archivedItems: any[] = (archivedData as any)?.items || [];

  // Get list of archived ASINs for filtering (items that have been explicitly archived)
  const archivedAsins = new Set(archivedItems.map((item: any) => item.asin));

  // Filter out rows where essential fields are blank AND exclude archived items
  // Also keep track of original row indices
  const validItems = sourcingItems
    .map((item: GoogleSheetsSourcingItem, originalIndex: number) => ({
      ...item,
      _originalRowIndex: originalIndex,
    }))
    .filter(
      (item: GoogleSheetsSourcingItem & { _originalRowIndex: number }) => {
        const productName = item["Product Name"]?.trim();
        const asin = item["ASIN"]?.trim();
        const hasValidData =
          productName && productName !== "" && asin && asin !== "";
        const isNotArchived = !archivedAsins.has(asin);
        return hasValidData && isNotArchived;
      },
    );

  // Update Product Review (Winner status)
  const updateProductReview = useMutation({
    mutationFn: async ({
      rowIndex,
      productReview,
    }: {
      rowIndex: number;
      productReview: string;
    }) => {
      return apiRequest(
        `/api/sourcing/sheets/${rowIndex}/product-review`,
        "PATCH",
        {
          productReview,
        },
      );
    },
    onSuccess: (data, variables) => {
      toast({
        title: "Success",
        description: `Product marked as ${variables.productReview}`,
      });
      queryClient.invalidateQueries({ queryKey: ["/api/sourcing/sheets"] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to update product review",
        variant: "destructive",
      });
    },
  });

  // Update Sourcing Method
  const updateSourcingMethod = useMutation({
    mutationFn: async ({
      rowIndex,
      sourcingMethod,
    }: {
      rowIndex: number;
      sourcingMethod: string;
    }) => {
      return apiRequest(
        `/api/sourcing/sheets/${rowIndex}/sourcing-method`,
        "PATCH",
        {
          sourcingMethod,
        },
      );
    },
    onSuccess: (data, variables) => {
      toast({
        title: "Success",
        description: `Sourcing method updated to "${variables.sourcingMethod}"`,
      });
      queryClient.invalidateQueries({ queryKey: ["/api/sourcing/sheets"] });
    },
    onError: (error) => {
      console.error("‚ùå Error updating sourcing method:", error);
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to update sourcing method",
        variant: "destructive",
      });
    },
  });

  // Update Notes
  const updateNotes = useMutation({
    mutationFn: async ({
      rowIndex,
      notes,
    }: {
      rowIndex: number;
      notes: string;
    }) => {
      return apiRequest(`/api/sourcing/sheets/${rowIndex}/notes`, "PATCH", {
        notes,
      });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Notes updated successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/sourcing/sheets"] });
      setNotesModalOpen(false);
      setEditingNotes(null);
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to update notes",
        variant: "destructive",
      });
    },
  });

  // File upload mutations
  const uploadFile = useMutation({
    mutationFn: async ({ rowIndex, asin, file }: { rowIndex: number; asin: string; file: File }) => {
      const formData = new FormData();
      formData.append('file', file);
      formData.append('rowIndex', rowIndex.toString());
      formData.append('asin', asin);

      const response = await fetch('/api/sourcing/files/upload', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Upload failed');
      }

      return response.json();
    },
    onSuccess: (data, variables) => {
      toast({
        title: "Success", 
        description: "File uploaded successfully"
      });
      queryClient.invalidateQueries({ queryKey: ['/api/sourcing/files', variables.rowIndex] });
    },
    onError: (error: any) => {
      console.error('Error uploading file:', error);
      toast({
        title: "Error",
        description: error?.message || "Failed to upload file",
        variant: "destructive",
      });
    },
  });

  const deleteFile = useMutation({
    mutationFn: async (fileId: string) => {
      return apiRequest(`/api/sourcing/files/${fileId}`, "DELETE");
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "File deleted successfully"
      });
      // Need to invalidate all file queries since we don't know which row this file belongs to
      queryClient.invalidateQueries({ queryKey: ['/api/sourcing/files'] });
    },
    onError: (error: any) => {
      console.error('Error deleting file:', error);
      toast({
        title: "Error",
        description: error?.message || "Failed to delete file",
        variant: "destructive",
      });
    },
  });

  // Save items to database
  const saveItemsToDatabase = useMutation({
    mutationFn: async (items: any[]) => {
      return apiRequest("/api/sourcing/items/save", "POST", items);
    },
    onSuccess: () => {
      console.log("‚úÖ Items saved to database successfully");
      toast({
        title: "Success",
        description: "Items saved to database successfully",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to save items to database",
        variant: "destructive",
      });
    },
  });

  // Archive item with optimistic updates
  const archiveItem = useMutation({
    mutationFn: async (rowIndex: number) => {
      return apiRequest(`/api/sourcing/items/${rowIndex}/archive`, "POST");
    },
    onMutate: async (rowIndex: number) => {
      console.log(
        "üéØ OPTIMISTIC ARCHIVE - Starting onMutate for rowIndex:",
        rowIndex,
      );

      // Cancel outgoing refetches so they don't overwrite our optimistic update
      await queryClient.cancelQueries({ queryKey: ["/api/sourcing/sheets"] });
      console.log("üéØ OPTIMISTIC ARCHIVE - Cancelled queries");

      // Snapshot the previous value for rollback
      const previousData = queryClient.getQueryData([
        "/api/sourcing/sheets",
      ]) as SheetsData | undefined;
      console.log(
        "üéØ OPTIMISTIC ARCHIVE - Previous data:",
        previousData?.items?.length,
        "items",
      );

      // Optimistically update to remove the item being archived
      queryClient.setQueryData(["/api/sourcing/sheets"], (old: any) => {
        console.log(
          "üéØ OPTIMISTIC ARCHIVE - Old data before filter:",
          old?.items?.length,
          "items",
        );
        if (!old?.items) {
          console.log(
            "üéØ OPTIMISTIC ARCHIVE - No items in old data, returning old",
          );
          return old;
        }

        // Find the item that is being archived by _originalRowIndex
        // The rowIndex parameter is the _originalRowIndex from the clicked item
        const itemToRemove = old.items[rowIndex];
        console.log(
          "üéØ OPTIMISTIC ARCHIVE - Item to remove at rowIndex",
          rowIndex,
          ":",
          {
            ASIN: itemToRemove?.ASIN,
            ProductName:
              itemToRemove?.["Product Name"]?.substring(0, 50) + "...",
          },
        );

        // Filter out the item at the specific rowIndex
        const filteredItems = old.items.filter((item: any, index: number) => {
          const shouldKeep = index !== rowIndex;
          if (!shouldKeep) {
            console.log(
              "üéØ OPTIMISTIC ARCHIVE - Removing item at index:",
              index,
              "ASIN:",
              item?.ASIN,
            );
          }
          return shouldKeep;
        });

        console.log(
          "üéØ OPTIMISTIC ARCHIVE - Filtered to:",
          filteredItems.length,
          "items (removed 1)",
        );

        return {
          ...old,
          items: filteredItems,
        };
      });

      // Verify the update worked
      const updatedData = queryClient.getQueryData(["/api/sourcing/sheets"]) as
        | SheetsData
        | undefined;
      console.log(
        "üéØ OPTIMISTIC ARCHIVE - Updated data after setQueryData:",
        updatedData?.items?.length,
        "items",
      );

      return { previousData };
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Item archived successfully",
      });
      // Invalidate both queries to refresh the data
      queryClient.invalidateQueries({ queryKey: ["/api/sourcing/items"] });
      queryClient.invalidateQueries({ queryKey: ["/api/sourcing/sheets"] });
    },
    onError: (error, rowIndex, context) => {
      // Revert the optimistic update
      if (context?.previousData) {
        queryClient.setQueryData(
          ["/api/sourcing/sheets"],
          context.previousData,
        );
      }

      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to archive item",
        variant: "destructive",
      });
    },
  });

  // Delete item with optimistic updates
  const deleteItem = useMutation({
    mutationFn: async (rowIndex: number) => {
      return apiRequest(`/api/sourcing/items/${rowIndex}`, "DELETE");
    },
    onMutate: async (rowIndex: number) => {
      // Cancel outgoing refetches so they don't overwrite our optimistic update
      await queryClient.cancelQueries({ queryKey: ["/api/sourcing/sheets"] });

      // Snapshot the previous value for rollback
      const previousData = queryClient.getQueryData(["/api/sourcing/sheets"]);

      // Optimistically update to remove the item being deleted
      queryClient.setQueryData(["/api/sourcing/sheets"], (old: any) => {
        if (!old?.items) return old;

        // Filter out the item at the specific rowIndex
        const filteredItems = old.items.filter((item: any, index: number) => {
          // Remove the item at the rowIndex being deleted
          return index !== rowIndex;
        });

        return {
          ...old,
          items: filteredItems,
        };
      });

      return { previousData };
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Item deleted successfully",
      });
      // Refetch to get the accurate data
      queryClient.invalidateQueries({ queryKey: ["/api/sourcing/sheets"] });
    },
    onError: (error, rowIndex, context) => {
      // Revert the optimistic update
      if (context?.previousData) {
        queryClient.setQueryData(
          ["/api/sourcing/sheets"],
          context.previousData,
        );
      }

      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to delete item",
        variant: "destructive",
      });
    },
  });

  const handleEditNotes = (
    rowIndex: number,
    item: GoogleSheetsSourcingItem,
  ) => {
    setEditingNotes({ rowIndex, item, currentNotes: item.Notes || "" });
    setNotesText(item.Notes || "");
    setNotesModalOpen(true);
  };

  const handleSaveNotes = () => {
    if (editingNotes) {
      updateNotes.mutate({
        rowIndex: editingNotes.rowIndex,
        notes: notesText,
      });
    }
  };

  const handleMarkAsWinner = (rowIndex: number) => {
    updateProductReview.mutate({
      rowIndex,
      productReview: "Winner",
    });
  };

  const handleArchiveItem = (
    item: GoogleSheetsSourcingItem & { _originalRowIndex: number },
  ) => {
    console.log(
      "üì¶ Archiving item with ASIN:",
      item["ASIN"],
      "Original row:",
      item._originalRowIndex,
    );
    archiveItem.mutate(item._originalRowIndex);
  };

  const handleDeleteItem = (
    item: GoogleSheetsSourcingItem & { _originalRowIndex: number },
  ) => {
    if (
      confirm(
        "Are you sure you want to delete this item? This will remove it from Google Sheets and cannot be undone.",
      )
    ) {
      console.log(
        "üóëÔ∏è Deleting item with original row index:",
        item._originalRowIndex,
      );
      deleteItem.mutate(item._originalRowIndex);
    }
  };

  // Auto-sync filtered items to database
  const syncItemsToDatabase = () => {
    if (validItems.length > 0) {
      const itemsToSave = validItems.map(
        (
